    // 点评后计算待点评值
    function calcNum(num) {
        if (num <= 0) {
            return "";
        } else {
            return "(<em>" + num + "</em>)";
        }
    }

    function saveComment(data){
	    var fabu = new fabuStatus();
       	$.ajax({
            url: 'http://www.lvmama.com/cmt_front/comment/ajaxWriteComment/saveComment.do',
            traditional: true,
            type: 'post',
            dataType: 'json',
            data: data,
            success :function (result) {
            	try{clearInterval(fabu.timer);}catch(e){};
                if (result.flag) {
                    //待点评数
                    callback(result);
                }else{
                    alert(result.message == "" ? "写点评失败，请稍后再试!" : result.message);
                }
            }
        });
    }

	//点评发布状态
	function fabuStatus() {
	    var $fabuBtn = $('.JS_inset');
	    var i = 0;
	    var value = '发布中';
	    this.timer = setInterval(function() {
	        i == 0 && (value = '发布中');
	        i == 1 && (value = '发布中.');
	        i == 2 && (value = '发布中..');
	        i == 3 && (value = '发布中...');
	        i = i > 3 ? 0 : i+1;
	        $fabuBtn.val(value);
	    }, 300);
	};
</script>
