<script>
	$(function(){
        //弹出会员升级权益提醒
        function showVipUpgradePrompt(){
            var _scrolltop = $(document).scrollTop()+150;
            var height_w =$(document).height();
            $('.pop_body_bg').css({'height':height_w,'width':$(document.body).width()}).show();
            $('.grade_bg').show().css({'top':_scrolltop});
        };

        //关闭提醒
        $('.close').click(function(){
            closeAndUpdatePrompt('CLOSE_DIALOG')
        });

        //我知道了
        $('.know_btn').click(function(){
            closeAndUpdatePrompt('I_KNOW')
        });

        //了解特权
        $('.konw_wz').click(function(){
            closeAndUpdatePrompt('READ_MORE')
			window.open("/myspace/membercenter/myMemberCenter.do#benefit")
        });

		//关闭和更新提醒
		function closeAndUpdatePrompt(actionType){
            $('.pop_body_bg,.grade_bg').hide();
            updateVipUpgradePrompt(actionType)
		}

        //更新会员升级权益提醒记录
        function updateVipUpgradePrompt(actionType){
            $.ajax({
                url:'/myspace/membercenter/modifyVipUpgradePrompt.do',
                type:'POST',
                dataType:'json',
                data:{
                    'actionType':actionType
                },
                success:function(data) {
                    if (!data.success) {
                        ptnError("updateVipUpgradePrompt 服务器端异常!");
                    }
                },
                error:function(){
                    ptnError("updateVipUpgradePrompt 网络异常!");
                }
            });
        }

        //查询会员升级权益提醒记录
        function findVipUpgradePrompt(currentLevel){
            if(currentLevel > 0) {
                $.ajax({
                    url:'/myspace/membercenter/findVipUpgradePrompt.do',
                    type:'POST',
                    dataType:'json',
                    success:function(data) {
                        if (data.success && data.upLevel != null) {
                            //显示权益提醒弹窗
                            showVipUpgradePrompt()
                        }else if(!data.success){
                            ptnError("findVipUpgradePrompt 服务器端异常!");
                        }
                    },
                    error:function(){
                        ptnError("findVipUpgradePrompt 网络异常!");
                    }
                });
            }
        }

		//打印错误信息
		function ptnError(errorMsg){
            console.error(errorMsg);
		}

		//初始化查询
        findVipUpgradePrompt(0)
	});
</script>

